const initialProducts = generateProducts();

// --- COMPONENTS ---

const StarRating = ({ rating, reviews }) => (
    <div className="star-rating-container">
        <div className="stars">
            {[...Array(5)].map((_, i) => <Star key={i} size={16} className={`star ${i < Math.floor(rating) ? 'filled' : ''}`} />)}
        </div>
        <span className="reviews">({reviews})</span>
    </div>
);

const Header = ({ onSearch, cartItemCount, onCartClick }) => (
    <header className="header">
        <div className="header-container">
            <h1 className="header-logo">Kredo</h1>
            <div className="search-bar-wrapper">
                <div className="search-bar-container">
                    <Search className="search-icon" />
                    <input type="text" placeholder="Search for products..." onChange={(e) => onSearch(e.target.value)} className="search-input" />
                </div>
            </div>
            <div className="header-actions">
                <button className="header-button" onClick={onCartClick}>
                    <ShoppingCart />
                    {cartItemCount > 0 && <span className="cart-badge">{cartItemCount}</span>}
                </button>
                <Link to="/Accounts" className="header-button">
                    <User />
                </Link>
            </div>
        </div>
    </header>
);

const Banner = ({ activeCategory, onSelectCategory }) => (
    <div className="banner">
        <div className="banner-container">
            <h2 className="banner-title">Kredo</h2>
            <p className="banner-tagline">Your Style, Your Store</p>
            <div className="banner-categories">
                 <button onClick={() => onSelectCategory('All')} className={`category-button ${activeCategory === 'All' ? 'active' : ''}`}>All</button>
                {categories.map(cat => (
                    <button key={cat} onClick={() => onSelectCategory(cat)} className={`category-button ${activeCategory === cat ? 'active' : ''}`}>{cat}</button>
                ))}
            </div>
        </div>
    </div>
);

const ProductCard = ({ product, onProductClick, onAddToCart, onBuyNow }) => (
    <div className="product-card">
        <img src={product.image} alt={product.name} className="product-image" onClick={() => onProductClick(product)} />
        <div className="product-info">
            <h3 className="product-name">{product.name}</h3>
            <div className="product-rating"><StarRating rating={product.rating} reviews={product.reviews} /></div>
            <p className="product-price">{product.price.toLocaleString('en-IN')}</p>
            <div className="product-buttons">
                <button onClick={() => onAddToCart(product)} className="btn btn-add-cart">Add to Cart</button>
                <button onClick={() => onBuyNow(product)} className="btn btn-buy-now">Buy Now</button>
            </div>
        </div>
    </div>
);

const ProductDetailModal = ({ product, onClose, onAddToCart, onBuyNow }) => {
    if (!product) return null;
    return (
        <div className="modal-overlay" onClick={onClose}>
            <div className="modal-content product-detail-modal" onClick={e => e.stopPropagation()}>
                <img src={product.image} alt={product.name} className="product-detail-image" />
                <div className="product-detail-info">
                    <h2 className="product-detail-name">{product.name}</h2>
                    <div className="product-detail-rating"><StarRating rating={product.rating} reviews={product.reviews} /></div>
                    <p className="product-detail-description">{product.description}</p>
                    <p className="product-detail-price">{product.price.toLocaleString('en-IN')}</p>
                    <div className="product-detail-buttons">
                        <button onClick={() => onAddToCart(product)} className="btn btn-add-cart">Add to Cart</button>
                        <button onClick={() => onBuyNow(product)} className="btn btn-buy-now">Buy Now</button>
                    </div>
                </div>
            </div>
        </div>
    );
};

const CartModal = ({ isOpen, onClose, cart, onUpdateCart, onCheckout }) => {
    if (!isOpen) return null;
    const total = cart.reduce((sum, item) => sum + item.price * item.quantity, 0);

    return(
        <div className="modal-overlay" onClick={onClose}>
            <div className="modal-content cart-modal" onClick={e => e.stopPropagation()}>
                <div className="modal-header">
                    <h2 className="modal-title">Your Cart</h2>
                    <button onClick={onClose} className="modal-close-btn"><X /></button>
                </div>
                {cart.length > 0 ? (
                    <>
                        <div className="cart-items">
                            {cart.map(item => (
                                <div key={item.id} className="cart-item">
                                    <img src={item.image} alt={item.name} className="cart-item-image" />
                                    <div className="cart-item-details">
                                        <p>{item.name}</p>
                                        <p className="cart-item-price">{item.price.toLocaleString('en-IN')}</p>
                                    </div>
                                    <div className="cart-item-quantity">
                                        <button onClick={() => onUpdateCart(item.id, item.quantity - 1)}><Minus size={16} /></button>
                                        <span>{item.quantity}</span>
                                        <button onClick={() => onUpdateCart(item.id, item.quantity + 1)}><Plus size={16} /></button>
                                    </div>
                                    <p className="cart-item-total">{(item.price * item.quantity).toLocaleString('en-IN')}</p>
                                </div>
                            ))}
                        </div>
                        <div className="cart-footer">
                            <div className="cart-total">
                                <strong>Total:</strong>
                                <strong>{total.toLocaleString('en-IN')}</strong>
                            </div>
                            <button onClick={onCheckout} className="btn btn-buy-now checkout-btn">Proceed to Checkout</button>
                        </div>
                    </>
                ) : (
                    <p className="empty-cart-message">Your cart is empty.</p>
                )}
            </div>
        </div>
    );
};

const CheckoutModal = ({ isOpen, onClose }) => {
    const [step, setStep] = useState('address');
    const [paymentMethod, setPaymentMethod] = useState('card');
    if (!isOpen) return null;
    const PaymentOption = ({ method, icon, label }) => (<button onClick={() => setPaymentMethod(method)} className={`payment-option ${paymentMethod === method ? 'active' : ''}`}>{icon}<span className="payment-label">{label}</span></button>);
    
    return (
        <div className="modal-overlay">
            <div className="modal-content checkout-modal" onClick={e => e.stopPropagation()}>
                <div className="modal-header">
                    <button onClick={onClose} className="modal-close-btn absolute-close"><X /></button>
                    {step === 'address' && <h2 className="modal-title">Delivery Details</h2>}
                    {step === 'payment' && <h2 className="modal-title">Choose Payment Method</h2>}
                </div>
                {step === 'address' && (
                    <form className="checkout-form" onSubmit={(e) => { e.preventDefault(); setStep('payment'); }}>
                        <input type="text" placeholder="Full Name" required /><textarea placeholder="Shipping Address" rows="3" required /><input type="text" placeholder="City" required /><input type="text" placeholder="Postal Code" required />
                        <button type="submit" className="btn btn-buy-now">Continue to Payment</button>
                    </form>
                )}
                {step === 'payment' && (
                    <div className="payment-step">
                        <div className="payment-options">
                            <PaymentOption method="card" icon={<CreditCard />} label="Credit/Debit Card" />
                            <PaymentOption method="upi" icon={<Wallet />} label="UPI Payment" />
                            <PaymentOption method="cod" icon={<Landmark />} label="Cash on Delivery" />
                        </div>
                        <button onClick={() => setStep('success')} className="btn btn-confirm-order">Confirm Order</button>
                        <button onClick={() => setStep('address')} className="back-button">Back to Address</button>
                    </div>
                )}
                {step === 'success' && (
                    <div className="success-step">
                        <CheckCircle className="success-icon" />
                        <h2 className="success-title">Order Placed!</h2>
                        <p>Thank you for your purchase.</p>
                        <button onClick={onClose} className="btn btn-close-success">Close</button>
                    </div>
                )}
            </div>
        </div>
    );
};

