export default function Dashboard() {
    const [searchQuery, setSearchQuery] = useState('');
    const [activeCategory, setActiveCategory] = useState('All');
    const [sortBy, setSortBy] = useState('popularity');
    const [selectedProduct, setSelectedProduct] = useState(null);
    const [isCheckoutOpen, setIsCheckoutOpen] = useState(false);
    const [isCartOpen, setIsCartOpen] = useState(false);
    const [cart, setCart] = useState([]);

    const handleBuyNow = () => {
        setSelectedProduct(null);
        setIsCheckoutOpen(true);
    };

    const handleAddToCart = (product) => {
        setCart(prevCart => {
            const existingItem = prevCart.find(item => item.id === product.id);
            if (existingItem) {
                return prevCart.map(item => item.id === product.id ? { ...item, quantity: item.quantity + 1 } : item);
            }
            return [...prevCart, { ...product, quantity: 1 }];
        });
    };

    const handleUpdateCart = (productId, quantity) => {
        if (quantity < 1) {
            setCart(prev => prev.filter(item => item.id !== productId));
        } else {
            setCart(prev => prev.map(item => item.id === productId ? { ...item, quantity } : item));
        }
    };
    
    const handleCheckout = () => {
        setIsCartOpen(false);
        setIsCheckoutOpen(true);
    };

    const filteredAndSortedProducts = useMemo(() => {
        let products = initialProducts.filter(p => activeCategory === 'All' || p.category === activeCategory);
        if (searchQuery) { products = products.filter(p => p.name.toLowerCase().includes(searchQuery.toLowerCase())); }
        return products.sort((a, b) => {
            switch (sortBy) {
                case 'price-asc': return a.price - b.price;
                case 'price-desc': return b.price - a.price;
                case 'newest': return b.createdAt - a.createdAt;
                case 'popularity': default: return b.reviews - a.reviews;
            }
        });
    }, [searchQuery, activeCategory, sortBy]);

    const cartItemCount = cart.reduce((total, item) => total + item.quantity, 0);

    return (
        <div className="dashboard-container">
            <Header onSearch={setSearchQuery} cartItemCount={cartItemCount} onCartClick={() => setIsCartOpen(true)} />
            <main>
                <Banner activeCategory={activeCategory} onSelectCategory={setActiveCategory} />
                <div className="main-content">
                    <div className="sort-container">
                        <select value={sortBy} onChange={e => setSortBy(e.target.value)} className="sort-select">
                            <option value="popularity">Popularity</option>
                            <option value="price-asc">Price: Low to High</option>
                            <option value="price-desc">Price: High to Low</option>
                            <option value="newest">Newest First</option>
                        </select>
                    </div>
                    <div className="product-grid">
                        {filteredAndSortedProducts.map(product => <ProductCard key={product.id} product={product} onProductClick={setSelectedProduct} onAddToCart={handleAddToCart} onBuyNow={handleBuyNow} />)}
                    </div>
                </div>
            </main>
            <Footer />
            <ProductDetailModal product={selectedProduct} onClose={() => setSelectedProduct(null)} onAddToCart={handleAddToCart} onBuyNow={handleBuyNow} />
            <CartModal isOpen={isCartOpen} onClose={() => setIsCartOpen(false)} cart={cart} onUpdateCart={handleUpdateCart} onCheckout={handleCheckout} />
            <CheckoutModal isOpen={isCheckoutOpen} onClose={() => setIsCheckoutOpen(false)} />
        </div>
    );
}
 